// Mock data for testing - Replace with actual scraping
const companies = [
  {
    company_name: 'WeCP',
    company_linkedin: 'https://www.linkedin.com/company/wecp/',
    company_website: 'wecp.com',
    company_size: '2-10',
    hiring_role: 'Software Developer',
    job_description: 'Looking for full-stack developer to build our assessment platform'
  },
  {
    company_name: 'TechStartup',
    company_linkedin: 'https://www.linkedin.com/company/techstartup/',
    company_website: 'techstartup.io',
    company_size: '5-10',
    hiring_role: 'Senior Developer',
    job_description: 'Need developer for MVP development, automation focus'
  }
];

return companies.map(company => ({ json: company }));

// Get Snov.io response
const snovResponse = $input.item.json;
const companyData = $('2. Extract Companies (2-10 employees)').item.json;

// Extract emails from Snov.io response
const emails = snovResponse.data?.emails || snovResponse.emails || [];

if (emails.length === 0) {
  console.log('No emails found for:', companyData.company_name);
  return [];
}

// Find founder/CEO/CTO
const founderKeywords = ['founder', 'ceo', 'cto', 'chief executive', 'co-founder', 'owner'];

let founderEmail = emails.find(email => {
  const position = (email.position || '').toLowerCase();
  return founderKeywords.some(keyword => position.includes(keyword));
});

// Fallback: take first email if no founder found
if (!founderEmail) {
  founderEmail = emails[0];
}

if (!founderEmail || !founderEmail.email) {
  console.log('No valid email found');
  return [];
}

return [{
  json: {
    company_name: companyData.company_name,
    company_website: companyData.company_website,
    company_linkedin: companyData.company_linkedin,
    hiring_role: companyData.hiring_role,
    job_description: companyData.job_description,
    founder_name: `${founderEmail.firstName || ''} ${founderEmail.lastName || ''}`.trim() || 'there',
    founder_email: founderEmail.email,
    founder_position: founderEmail.position || 'Founder',
    found_via: 'snov.io'
  }
}];

// Parse Gemini AI research response
const geminiResponse = $input.item.json;
const companyData = $input.item.json;

let insights = {};

try {
  const text = geminiResponse.candidates[0].content.parts[0].text;

  // Remove markdown code blocks if present
  const cleanText = text.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();

  // Extract JSON
  const jsonMatch = cleanText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    insights = JSON.parse(jsonMatch[0]);
  }
} catch (error) {
  console.log('AI parsing error, using fallback:', error);
  // Fallback insights
  insights = {
    pain_points: [
      'Struggling to find reliable developers',
      'Long development timelines affecting time-to-market',
      'High agency costs eating into runway'
    ],
    automation_opportunities: [
      'Manual testing and deployment processes',
      'Repetitive admin and data entry tasks'
    ],
    performance_gaps: [
      'Website loading slowly affecting conversions',
      'Missing key features competitors have'
    ],
    cost_cutting_potential: 'Can save $60,000-$120,000 by choosing MVP approach over full agency build',
    scalability_concerns: [
      'Current architecture won\'t handle 10x growth',
      'Technical debt from rushing initial build'
    ],
    our_solution_fit: 'We can build their MVP in 5-6 weeks, include automation, and save 70% on costs',
    personalized_hook: `I noticed ${companyData.company_name} is hiring for ${companyData.hiring_role}`
  };
}

return [{
  json: {
    ...companyData,
    ai_insights: insights
  }
}];

// Extract email from Gemini response
const geminiResponse = $input.item.json;
const companyData = $input.item.json;

let emailBody = '';

try {
  emailBody = geminiResponse.candidates[0].content.parts[0].text;
  // Clean up markdown
  emailBody = emailBody.replace(/```html\n?/g, '').replace(/```\n?/g, '').trim();
} catch (error) {
  console.log('Email extraction error, using fallback');
  // Fallback email
  const insights = companyData.ai_insights;
  emailBody = `Hi ${companyData.founder_name},<br><br>

  ${insights.personalized_hook}. After researching ${companyData.company_name}, I noticed you're dealing with ${insights.pain_points[0]} - this is exactly what we solve at Ready Launch.<br><br>

  <strong>Here's what we can do:</strong><br>
  â€¢ Build your MVP in 5-6 weeks instead of 6 months<br>
  â€¢ ${insights.cost_cutting_potential}<br>
  â€¢ ${insights.automation_opportunities[0]}<br><br>

  We recently built a Sora AI-powered video tool where users upload photos and interact with Krishna from Mahabharata - delivered in 5 weeks at 1/10th agency cost.<br><br>

  Given your focus on ${companyData.hiring_role}, I'd love to show you how we can launch your product faster and save you $60k-120k in the process.<br><br>

  Open to a quick 15-min chat this week?<br><br>

  Best regards,<br>
  [Your Name]<br>
  Ready Launch`;
}

return [{
  json: {
    ...companyData,
    email_body: emailBody,
    email_subject: `Quick question about ${companyData.company_name}'s development needs`,
    status: 'ready_to_send',
    created_date: new Date().toISOString()
  }
}];

// Detect positive replies
const positiveKeywords = [
  'interested', 'yes', 'let\'s talk', 'tell me more',
  'schedule', 'available', 'sounds good', 'call',
  'meeting', 'chat', 'discuss', 'open to', 'sure',
  'love to', 'would like', 'happy to'
];

const email = $input.item.json;
const emailBody = (email.snippet || '').toLowerCase();
const subject = (email.subject || '').toLowerCase();

const isPositive = positiveKeywords.some(keyword => 
  emailBody.includes(keyword) || subject.includes(keyword)
);

if (!isPositive) {
  return [];
}

// Extract email address
const fromEmail = email.from?.match(/([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/)?.[0] || email.from;

return [{
  json: {
    from_email: fromEmail,
    from_name: email.from,
    subject: email.subject,
    snippet: email.snippet,
    message_id: email.id,
    reply_type: 'positive',
    received_date: new Date().toISOString()
  }
}];

