// Check which leads need follow-ups
const lead = $input.item.json;
const today = new Date();
const lastContact = new Date(lead.last_contact_date);
const daysSince = Math.floor((today - lastContact) / (1000 * 60 * 60 * 24));
const followUpCount = parseInt(lead.follow_up_count) || 0;
const followUpSchedule = [1, 3, 5, 7]; // Days 1, 3, 5, 7

// Skip if already converted or too many follow-ups
if (lead.status === 'converted' || followUpCount >= 4) {
  return [];
}

// Check if today matches follow-up day
if (followUpSchedule[followUpCount] === daysSince) {
  return [{
    json: {
      ...lead,
      days_since_contact: daysSince,
      follow_up_number: followUpCount + 1
    }
  }];
}

return [];
