// Extract company names and job URLs from LinkedIn HTML
const html = $input.first().json.body;
const companies = [];

// Parse HTML to find company info and job links
// This is a simplified version - adjust regex based on actual LinkedIn HTML structure
const companyPattern = /<span class="result-card__subtitle-link"[^>]*>([^<]+)<\/span>/gi;
const jobLinkPattern = /href="(https:\/\/www\.linkedin\.com\/jobs\/view\/\d+[^"]*)"/gi;

let companyMatch;
let linkMatch;
const companyNames = [];
const jobLinks = [];

while ((companyMatch = companyPattern.exec(html)) !== null) {
  companyNames.push(companyMatch[1].trim());
}

while ((linkMatch = jobLinkPattern.exec(html)) !== null) {
  jobLinks.push(linkMatch[1]);
}

// Combine and create output (limit to 20 companies)
for (let i = 0; i < Math.min(companyNames.length, 20); i++) {
  companies.push({
    json: {
      company_name: companyNames[i],
      job_url: jobLinks[i] || '',
      scraped_date: new Date().toISOString(),
      status: 'scraped'
    }
  });
}

return companies;

// Extract company website and hiring details from job page
const html = $input.first().json.body;
const previousData = $input.first().json;

// Extract company website
const websiteMatch = html.match(/https?:\/\/(?:www\.)?([a-zA-Z0-9-]+\.[a-zA-Z]{2,})/i);
const companyWebsite = websiteMatch ? websiteMatch[0] : '';

// Extract job description/requirements
const descMatch = html.match(/<div class="description[^>]*>([\s\S]*?)<\/div>/i);
const jobDescription = descMatch ? descMatch[1].replace(/<[^>]*>/g, '').substring(0, 500) : '';

return [{
  json: {
    ...previousData,
    company_website: companyWebsite,
    job_description: jobDescription,
    company_domain: companyWebsite.replace(/https?:\/\/(www\.)?/, '').split('/')[0]
  }
}];

// Filter to get founder/CEO emails only
const emailData = $input.first().json;
const previousData = $input.first().json;
const founderKeywords = ['founder', 'ceo', 'cto', 'co-founder', 'chief executive', 'owner'];

if (!emailData.emails || emailData.emails.length === 0) {
  return []; // Skip if no emails found
}

// Find founder email
let founderEmail = emailData.emails.find(email => {
  const position = (email.position || '').toLowerCase();
  return founderKeywords.some(keyword => position.includes(keyword));
});

// Fallback: if no founder found, take first email
if (!founderEmail && emailData.emails.length > 0) {
  founderEmail = emailData.emails[0];
}

if (founderEmail && founderEmail.email) {
  return [{
    json: {
      company_name: previousData.company_name,
      company_website: previousData.company_website,
      job_description: previousData.job_description,
      founder_name: `${founderEmail.firstName || ''} ${founderEmail.lastName || ''}`.trim() || 'there',
      founder_email: founderEmail.email,
      founder_position: founderEmail.position || 'Founder',
      scraped_date: previousData.scraped_date
    }
  }];
}

return []; // Skip if no valid email

// Combine all research data for AI analysis
const previousData = $input.item(0).json;
const websiteData = $input.item(1)?.json?.body || '';
const linkedInData = $input.item(2)?.json?.body || '';

// Extract text content from HTML (remove tags)
const cleanText = (html) => {
  return html.replace(/<script[^>]*>.*?<\/script>/gi, '')
             .replace(/<style[^>]*>.*?<\/style>/gi, '')
             .replace(/<[^>]+>/g, ' ')
             .replace(/\s+/g, ' ')
             .trim()
             .substring(0, 3000); // Limit to 3000 chars
};

const websiteText = cleanText(websiteData);
const linkedInText = cleanText(linkedInData);

return [{
  json: {
    ...previousData,
    website_content: websiteText,
    linkedin_content: linkedInText
  }
}];

