// Parse Gemini AI response and extract insights
const geminiResponse = $input.first().json;
const previousData = $input.first().json;

let aiInsights = {};

try {
  // Gemini returns response in candidates[0].content.parts[0].text
  const responseText = geminiResponse.candidates[0].content.parts[0].text;

  // Extract JSON from response (remove markdown code blocks if present)
  const jsonMatch = responseText.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    aiInsights = JSON.parse(jsonMatch[0]);
  }
} catch (error) {
  // Fallback if AI parsing fails
  aiInsights = {
    pain_points: ['scaling development team', 'time-to-market pressure', 'budget constraints'],
    automation_needs: ['manual deployment processes', 'repetitive admin tasks'],
    performance_gaps: ['slow website load times', 'poor mobile experience'],
    cost_cutting_potential: 'MVP approach could save 60-70% vs full build',
    scalability_concerns: ['current architecture won\'t scale', 'technical debt accumulation'],
    our_solution_fit: 'Ready Launch can build scalable MVP in 4-6 weeks at fraction of agency cost',
    personalized_hook: `I noticed ${previousData.company_name} is hiring developers`
  };
}

return [{
  json: {
    ...previousData,
    ai_pain_points: aiInsights.pain_points || [],
    ai_automation_needs: aiInsights.automation_needs || [],
    ai_performance_gaps: aiInsights.performance_gaps || [],
    ai_cost_cutting: aiInsights.cost_cutting_potential || '',
    ai_scalability: aiInsights.scalability_concerns || [],
    ai_solution_fit: aiInsights.our_solution_fit || '',
    ai_hook: aiInsights.personalized_hook || ''
  }
}];

