  // Fallback email if AI fails
  emailBody = `Hi ${previousData.founder_name},<br><br>

  ${previousData.ai_hook}.<br><br>

  I spent some time researching ${previousData.company_name} and noticed you're facing ${previousData.ai_pain_points[0]}. This is exactly what we solve at Ready Launch.<br><br>

  <strong>Here's what we can do for you:</strong><br>
  â€¢ Build your MVP in 4-6 weeks (not 6 months)<br>
  â€¢ Save $40,000-$150,000 vs traditional agencies<br>
  â€¢ ${previousData.ai_automation_needs[0]}<br><br>

  We recently built a Sora AI-powered video tool where users interact with Krishna from Mahabharata - delivered in 5 weeks at 1/10th the quoted agency price.<br><br>

  Given your focus on ${previousData.ai_solution_fit}, I'd love to show you how we can accelerate your product launch while cutting costs dramatically.<br><br>

  Open to a quick 15-min chat this week?<br><br>

  Best regards,<br>
  [Your Name]<br>
  <strong>Ready Launch</strong>`;
}

return [{
  json: {
    ...previousData,
    personalized_email_body: emailBody
  }
}];

// Detect positive replies
const positiveKeywords = [
  'interested', 'yes', "let's talk", 'tell me more', 
  'schedule', 'available', 'sounds good', 'call',
  'meeting', 'chat', 'discuss', 'open to'
];

const results = [];

for (const item of $input.all()) {
  const emailBody = (item.json.snippet || '').toLowerCase();
  const subject = (item.json.subject || '').toLowerCase();
  const from = item.json.from || '';

  const isPositive = positiveKeywords.some(keyword => 
    emailBody.includes(keyword) || subject.includes(keyword)
  );

  if (isPositive) {
    results.push({
      json: {
        ...item.json,
        reply_type: 'positive',
        from_email: from.match(/([a-zA-Z0-9._-]+@[a-zA-Z0-9._-]+\.[a-zA-Z0-9_-]+)/)?.[0] || from
      }
    });
  }
}

return results;

